const AuthRouter = require("express").Router();
const check = require("./Routes/check")
const login = require("./Routes/login")
const logout = require("./Routes/logout")
const refresh = require("./Routes/refresh")
const register = require("./Routes/register")

//login a user via the login function
AuthRouter.post("/login", login);

//register a user and login in the user if register is successfull
AuthRouter.post("/register", register);

//logout a user on the server
AuthRouter.delete("/logout", logout);

//check the accessToken and refreshToken from the user
AuthRouter.post("/check", check);

//gerenate a new accessToken from the given refreshToken
AuthRouter.post("/refresh", refresh);

module.exports = AuthRouter